<script type="text/javascript">
$(function(){
    var comic_id = 142882;
    var comic_rand_num = 1;
    var comic_page = 0;
    get_recommend_comic();
    function get_recommend_comic(){
        $.ajax({
            url : "/comic/ajax.php?mod=comic&act=get_recommend_comic_info",
            data : {comic_rand_num:comic_rand_num,comic_page:comic_page,comic_num:6,comic_id:comic_id},
            type : 'get',
            dataType : 'json',
            success : function(data){
                if(data.is_recommend == 1){
                    var html = "";
                    var num = 0;
                    $.each(data.comic_info,function(k,v){
                        num ++;
                        html += "<a href='http://www.u17.com/comic/"+ v.comic_id + ".html' target='_blank' onclick='count_ad_click(67)'>";
                        html += "<span class='imgBox'><img src='"+ v.cover +"' /></span><span class='txt'>"+ v.name +"</span></a>";
                    });
                    $('.recommend_list').empty();
                    $('.recommend_list').append(html);

                    comic_page = data.comic_page;
                    comic_rand_num = 0;
                }else{
                    $('#left_empty').show();
                }
            }
        });
    }

    $('#huan_recommend_comic').click(function(){
        comic_page ++;
        get_recommend_comic();
    });
});


